import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter/rendering.dart';import 'package:projetoflutter/disciplinas.dart';import 'package:projetoflutter/noticias.dart';import 'package:projetoflutter/solicitacoes.dart';import 'package:projetoflutter/table.dart';void main() => runApp(MyApp());class MyApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    Widget titleSection = Container(      padding: const EdgeInsets.all(32),      child: Row(        children: [          Expanded(            /*1*/            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                /*2*/                Container(                  padding: const EdgeInsets.only(bottom: 8),                  child: Text(                    'Confira tudo pelo App',                    style: TextStyle(                      fontWeight: FontWeight.bold,                    ),                  ),                ),              ],            ),          ),        ],      ),    );    Color color = Theme.of(context).primaryColor;    Widget buttonSection = Container(      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceEvenly,        children: [          _buildButtonColumn(Colors.green, Icons.local_library, 'DISCIPLINAS'),          _buildButtonColumn(Colors.green, Icons.new_releases, 'NOTÍCIAS'),          _buildButtonColumn(Colors.green, Icons.add_alert, 'SOLICITAÇÕES'),          _buildButtonColumn(Colors.green, Icons.school, 'FREQUÊNCIA'),        ],      ),    );    Widget userSection = Container(      padding: const EdgeInsets.all(5),      child: Row(        mainAxisAlignment: MainAxisAlignment.start,        children: [        //  _buildButtonColumn(Colors.black, Icons.person, 'Luis'),           Text(            'Bem-Vindo!',          ),        ],      ),    );    Widget nameSection = Container(      padding: const EdgeInsets.all(5),      child: Row(        mainAxisAlignment: MainAxisAlignment.start,        children: [            _buildButtonColumn(Colors.black, Icons.person, 'Luis'),        ],      ),    );    Widget imageSection = Container(      padding: const EdgeInsets.all(5),      child: Row(        mainAxisAlignment: MainAxisAlignment.start,        children: [          _buildButtonColumn(Colors.black, Icons.person, 'Luis'),        ],      ),    );    return MaterialApp(      title: 'Flutter layout demo',      home: Scaffold(        appBar: AppBar(          title: Text('App Institucional'),          backgroundColor: Colors.green,          centerTitle: true,        ),        body: ListView(          children: [            userSection,            nameSection,            Image.asset(              'images/educacao.jpg',              width: 600,              height: 240,              fit: BoxFit.cover,            ),            titleSection,            buttonSection,          ],        ),        drawer:Builder(    builder: (context) =>  Drawer(          // Add a ListView to the drawer. This ensures the user can scroll          // through the options in the drawer if there isn't enough vertical          // space to fit everything.          child: ListView(            // Important: Remove any padding from the ListView.            padding: EdgeInsets.zero,            children: <Widget>[              ListTile(                title: Text('Disciplinas'),                onTap: () {                  Navigator.of(context).pop();                  Navigator.push(                    context,                    new MaterialPageRoute(                        builder: (BuildContext context) =>                        new Disciplinas()                    ),                  );                },              ),              ListTile(                title: Text('Notícias'),                onTap: () {                  Navigator.of(context).pop();                  Navigator.push(                    context,                    new MaterialPageRoute(                        builder: (BuildContext context) =>                        new Noticias(),                    ),                  );                },              ),              ListTile(                title: Text('Solicitações'),                onTap: () {                  Navigator.of(context).pop();                  Navigator.push(                    context,                    new MaterialPageRoute(                        builder: (BuildContext context) =>                        new Solicitacoes(),                    ),                  );                },              ),              ListTile(                title: Text('Frequência'),                onTap: () {                  Navigator.of(context).pop();                  Navigator.push(                    context,                    new MaterialPageRoute(                      builder: (BuildContext context) =>                      new TableFrequencia(),                    ),                  );                },              ),            ],          ),        ),      ),      ),    );  }  Column _buildButtonColumn(Color color, IconData icon, String label) {    return Column(      mainAxisSize: MainAxisSize.min,      mainAxisAlignment: MainAxisAlignment.center,      children: [        Icon(icon, color: color),        Container(          margin: const EdgeInsets.only(top: 8),          child: Text(            label,            style: TextStyle(              fontSize: 12,              fontWeight: FontWeight.w400,              color: color,            ),          ),        ),      ],    );  }}